(()=>{var o=class extends HTMLElement{constructor(){super();this.addEventListener("click",t=>{let n=this.querySelector(".cartQuickView__productLine-spinner"),e=this.querySelector("svg");n.style.display="block",e.style.display="none",t.preventDefault(),this.closest("cart-quick-view").updateQuantity(this.dataset.idproduct,0)})}};customElements.define("cart-remove-button",o);var c=class extends HTMLElement{constructor(){super();this.init()}init(){}getSectionsToRender(){return[{section:"cart-quickview",selector:".cartQuickView"},{section:"cart-icon-bubble",selector:".header__myCart-qty"}]}updateQuantity(t,n){fetch(window.Shopify.routes.root+"cart/change.js",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({id:t,quantity:n,sections:this.getSectionsToRender().map(e=>e.section),sections_url:window.location.pathname})}).then(e=>e.json()).then(e=>{this.getSectionsToRender().forEach(r=>{document.querySelectorAll(r.selector).forEach(i=>{let a=this.getSectionInnerHTML(e.sections[r.section]);i.replaceWith(a.querySelector(r.selector))})})}).catch(e=>{console.error("Error:",e)})}getSectionInnerHTML(t){return new DOMParser().parseFromString(t,"text/html")}};customElements.define("cart-quick-view",c);})();
